custom_css.post_content:
h1,h2,h3,h4,h5,h6 {
  line-height:1.1;
}

.footer .widget-title {font-size:22px}

h1 {font-size:74px;}

h2 {
    font-size:50px;
    margin-bottom:15px;
}

h3 {font-size:44px}

h4 {font-size:36px;}

h5 {font-size:30px}

h6 {font-size:22px}

.single-post h1 {font-size:54px}

@media (max-width:1024px) {
    h1:not(.product_title) {
			font-size:54px}
    .single-post h1 {
			font-size:48px}
	  h2 {font-size:42px}
	  h3 {font-size:36px}
	  h4 {font-size:32px;}
}

@media (max-width:479px) {
    h1 {font-size:44px !important}
    h2 {font-size:36px}
    h3 {font-size:32px}
    h4 {font-size:26px}
    h5 {font-size:22px}
    h6 {font-size:20px}
}

body {background:#fff}

h1,h2,h3,h4,h5,h6, .footer .widget-title {
	color:#3e3e3e;
}

@media (min-width:1025px) and (max-width:1470px) {
  /* Header H1 & p size mod */
	h1.elementor-icon-box-title {font-size:68px !important}
	.elementor-widget-themo-header .elementor-icon-box-content h1 ~ p {font-size:18px !important}
	/* Homepage header height mod */.elementor-648 .elementor-element.elementor-element-24cb0f46 > .elementor-container {
  min-height: 560px;
}
}

/*** Home overlap section */
.th-remove-hover a:hover,
.th-remove-hover a:active,
.th-remove-hover a:focus {text-decoration:none}

.btn, input[type=submit], .pager .previous > a, .pager .next > a {
  font-size:15px;
	font-weight: 400;
	letter-spacing:1.25px;
	text-transform:uppercase;
	border-radius:0;
}
.pager .previous > a, .pager .next > a {padding:14px 36px}

.th-cta .th-btn {font-size: 16px;}

/*** TEMP / COPY TO THEME */
@media (min-width: 480px) {
.elementor-widget-themo-header .elementor-icon-box-content h1 ~ .th-btn-wrap .th-btn {
    font-size: 15px;
    padding: 14px 40px;
	}
}

.themo_cart_icon a i, .headhesive--clone.themo_cart_icon a i, .headhesive--clone.banner[data-transparent-header="true"] .themo_cart_icon a i {color:#3a3a3a}

.th-pricing-title {
	font-weight:500}

@media (min-width:768px) {
  .navbar .navbar-nav .dropdown-menu {background-color:#fff;}

  .navbar .navbar-nav .dropdown-menu li a {
		color:#585858 !important}

  .navbar .navbar-nav .dropdown-menu li.active a,
  .navbar .navbar-nav .dropdown-menu li a:hover {
		color:#828282 !important}
}

.pager li > a, .pager li > span {border-color:#e1e1e1}

/*** Header styling */
#logo {margin-top:30px; margin-bottom:30px}
.navbar .navbar-nav > li > a {text-transform:uppercase}
.th-header-social a {padding-left: 11px}
.navbar-nav li.th-accent {margin-left:16px}

.navbar .navbar-toggle {top:28px;}
.headhesive--clone .navbar-toggle {top:14px !important;}

@media (max-width:820px) {
	.th-header-social {display:none}
}

/*** Footer styling */
.footer h3.widget-title {
	font-family:EB Garamond;
  font-weight:500; 
	margin-bottom: 20px;
}
.footer p, .footer ul li, .footer p a {font-size:14px}
.footer .th-contact-info-widget .icon-blocks p {line-height:160%}

/*** Sidebar styling */
.sidebar .widget-title {
	font-size:25px;
  color:#5f5f5f}
.sidebar .widget-title:after {height:1px}

body .sidebar table.booked-calendar th .monthName {font-size:16px}

.sidebar .widget .tagcloud a {
	color:#656565; 
	background-color: rgba(0,0,0,0.05)}
.sidebar .widget .tagcloud a:hover {
	background-color:#74986a;
	color:#fff;
}

/*** Sticky header styling */
.headhesive--clone {box-shadow: 0 0 20px 0 rgba(0,0,0,.06)}
.headhesive--clone #logo img {
	max-height: 34px !important
}
.headhesive--clone #logo {
    margin-top: 22px;
    margin-bottom: 22px;
}
.headhesive--clone .navbar-nav {
    margin-top: 15px !important;
}
.headhesive--clone .themo_cart_icon {
    margin-top: 23px;
}
.headhesive--clone .navbar-nav > li > a {color:#3a3a3a !important}

/*** Circle service block styling */
.elementor-view-framed .th-icon-size-sm .elementor-icon {border-width:2px}

/*** Map widget styling */
.map-info {
	border-radius:0;
	box-shadow: 0px 0px 30px 0px rgba(71,71,71,0.08);}

.map-info h3 {
	font-size:25px;
/*	font-family:Poppins; */
	color:#3E3E3E;
}
.map-info .th-gmap-address p,
.map-info .th-gmap-hoursop p {
    font-weight:500;
}
body.page-id-648 .map-info {top: 75px;}

/*** Masonry Blog styling */
body.page-id-648 .mas-blog-post .post-meta,
body.page-id-648 .mas-blog-post .show-comments,
body.page-id-648 .mas-blog-post .entry-content a,
body.page-id-648 .mas-blog-post .date-meta {display:none}

.mas-blog-post .show-comments {display:none}

.mas-blog-post img {
    /*border-radius: 5px 5px 0 0*/;
}
.mas-blog-post .post-inner {/*border-radius: 0 0 5px 5px;*/}

.mas-blog-post .post-title a {
	color:#515151;
	font-size:24px;
}
.mas-blog-post .entry-content p {font-size:15px}

.th-masonry-blog .format-quote .date-meta {display:none}
.th-masonry-blog .format-quote blockquote {margin-bottom:0}
.th-masonry-blog .format-quote blockquote:before {left:50px}
.th-masonry-blog .format-quote blockquote:after {right:50px; bottom:-45px;}

.th-masonry-blog .format-quote blockquote p {	font-size:25px;}

/*** Standard Blog / Single */
body.page-template-blog-standard .row .post:last-of-type .post-inner {border-bottom:0}

body.page-template-blog-standard footer.footer,
body.single-post footer.footer {margin-top:40px; border-top: 1px solid #dedede;}

.format-quote blockquote p {
	font-family:EB Garamond;
	font-size:28px;
	line-height:1.4
}

body.page-template-blog-standard .post .post-title a,
.single-post h1.entry-title {color:#3E3E3E}

@media (min-width:1025px) {
body.page-template-blog-standard .post h3.post-title {
	font-size:36px}
}

/*** Package styling */
.th-pkg-img img {
    /*border-radius: 5px 5px 0 0;*/ border-radius:0;
}
.th-pkg-content {
    /*border-radius: 0 0 5px 5px;*/
	border-radius:0;
}

body.page-id-648 .th-package .th-pkg-info {
	background-color:#9d9684 !important;
	border-radius:0;
	padding:14px;
}

.th-pkg-content h3 {
	/*font-family:Poppins;*/
	font-size:26px;
	color:#3E3E3E;
}
.th-pkg-info {/*border-radius:5px*/}
.th-pkg-info h4 {
	font-size:25px;
	font-family:Poppins;
}

@media (min-width:1085px) {
  .th-pkg-info {top:auto; bottom:215px; right:23px}
}

@media (min-width:1273px) {
  .th-pkg-info {top:auto; bottom:190px;}
}

/*** Accordion styling */
.elementor-accordion .elementor-tab-content {padding:15px 20px !important}

/*** Team styling */
.th-team-member img {
	/*border-radius:5px 5px 0 0*/}
.th-team-member-name {font-size:25px}
.th-team-member-content {
	padding:35px;
	/*border-radius:0 0 5px 5px*/}
.th-team-member-title {font-family:Poppins; font-size:15px}

@media (min-width:1215px) {
  .th-team-member-social {
    margin:0;
    position: absolute;
    top: 245px;
    right:24px;
  }
  .th-team-member-social i {
    font-size: 16px;
    color:#717171;
    opacity: 1;
    margin: 8px 6px 0 0;
    background: #fff;
    padding: 10px;
    width: 34px;
    height:34px;
    border-radius:0px;
    box-shadow:0px 0px 5px 0px rgba(0,0,0,0.20);
	  transition:all linear 0.15s;
  }
	.th-team-member-social i:hover {transform:translateY(-3px);
	}
}

/* Info Card widget */
.elementor-widget-themo-info-card .th-info-card-wrap {border-radius:0}

.elementor-widget-themo-info-card .th-info-card-wrap .elementor-icon-box-title {
	font-size: 34px;}

/* Form styling */
.frm_forms form input, .frm_forms form textarea {border-radius:0 !important}

/* Booked styling */
.th-cal-tooltip h3 {font-size:22px}

/* Lower contacts section */
@media (min-width:1025px) {
  .th-lower-contacts h4 {font-size:32px}
}

/* Course Guide
.item .th-port-highlight, .th-port-sub {display:none} */

.btn, .btn-cta, .btn-sm, .btn-group-sm > .btn, .btn-group-xs > .btn, .pager li > a, .pager li > span, .form-control, #respond input[type=submit], body .booked-modal button, .woocommerce #respond input#submit, .woocommerce a.button, .woocommerce button.button, .woocommerce input.button, .woocommerce div.product form.cart .button, .search-form input, .search-submit, .th-accent, .headhesive--clone.banner[data-transparent-header='true'] .th-accent {
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    border-radius: 2px;
}

/* Max width for slider title */
.page-id-676 .slides li:nth-child(3) h1.slider-title, .page-id-676 .slides li:nth-child(3) p {
    max-width: 550px;
}
