custom_css.post_content:
h2{
	font-size:40px;
	font-weight: 500;
}

h3{
	font-size:32px;
		font-weight: 500;
}

h4{
		font-weight: 500;
}

.footer p, .footer ul li, .footer p a {
    font-size: 14px;
}

.footer .widget-title {
	font-weight:600;
	font-size: 16px;
}

.navbar .navbar-nav > li > a {
    text-transform: uppercase;
}

.navbar .navbar-nav > li > a, .navbar .navbar-nav > li > a:hover, .navbar .navbar-nav > li.active > a, .navbar .navbar-nav > li.active > a:hover, .navbar .navbar-nav > li.active > a:focus, .navbar .navbar-nav > li.th-accent {
    font-size: 14px;
	  font-weight: 500;
	  color:#3a3a3a;

	/*font-size: 15px;
	  font-weight: 400;
	  color:#686868;*/
}

.themo_cart_icon {
    margin-top: calc(28px + 8px);
}

.themo_cart_icon a i, .headhesive--clone.themo_cart_icon a i, .headhesive--clone.banner[data-transparent-header="true"] .themo_cart_icon a i {
    color: #3a3a3a;
}

.headhesive--clone #logo img {
    max-height: 34px !important;
}

.headhesive--clone #logo {
    margin-top: 22px;
    margin-bottom: 22px;
}

.headhesive--clone .themo_cart_icon {
    margin-top: 23px;
}

.headhesive--clone .navbar-nav {
    margin-top: 15px !important;
}

.btn, .btn-cta, .btn-sm, .btn-group-sm > .btn, .btn-group-xs > .btn, .pager li > a, .pager li > span, .form-control, #respond input[type=submit], body .booked-modal button, .woocommerce #respond input#submit, .woocommerce a.button, .woocommerce button.button, .woocommerce input.button, .woocommerce div.product form.cart .button, .search-form input, .search-submit, .th-accent, .headhesive--clone.banner[data-transparent-header='true'] .th-accent {
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    border-radius: 2px;
}

/* Max width for slider title */
.page-id-676 .slides li:nth-child(3) h1.slider-title, .page-id-676 .slides li:nth-child(3) p {
    max-width: 550px;
}
